clear allclose all clcfileID = '/Users/Fred/Desktop/ProjetMonteCarloOK/cross.txt';fop=fopen(fileID, 'r');dates = textscan(fop,'%s %s');%,'delimiter','\n'Z=horzcat(dates{1:end})A=strfind(Z,'-');B=~cellfun(@isempty,A);C=cell2mat(A(B));D=find(B==1);for ii=1:length(Z(B))    [x y]=ind2sub(size(Z),D(ii));    Z{x,y}=strcat(Z{x,y}(1:C(ii)-1),'E',Z{x,y}(C(ii):end));endfclose(fop);datesNew=vec2mat(str2num(str2mat(Z')),size(Z,2));